// src/components/NewCommentForm.jsx
import React, { useState } from 'react';
import TextareaAutosize from 'react-textarea-autosize';
import { PaperAirplaneIcon } from '@heroicons/react/24/solid';

export default function NewCommentForm({ onSuccess, currentUser, itemId, setError }) {
  const [text, setText] = useState('');
  const [posting, setPosting] = useState(false);

  const handleAdd = async e => {
    e.preventDefault();
    if (!text.trim()) return;

    setPosting(true);
    try {
      const token = await currentUser.getIdToken();
      await api.post('/comments', { itemId, text }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setText('');
      onSuccess();
    } catch {
      setError('Could not post comment');
    } finally {
      setPosting(false);
    }
  };

  return (
    <form
      onSubmit={handleAdd}
      className="flex items-start space-x-4 bg-gray-50 p-4 rounded-2xl shadow-inner"
    >
      <div className="flex-shrink-0">
        <div className="h-10 w-10 rounded-full bg-blue-500 flex items-center justify-center text-white uppercase font-bold">
          {currentUser.email[0]}
        </div>
      </div>
      <div className="flex-1">
        <TextareaAutosize
          minRows={2}
          maxRows={6}
          className="w-full p-3 bg-white rounded-xl border focus:ring-2 focus:ring-blue-400 resize-none"
          placeholder="Write a commentâ€¦"
          value={text}
          onChange={e => setText(e.target.value)}
        />
        <div className="flex justify-end mt-2">
          <button
            type="submit"
            disabled={posting || !text.trim()}
            className={`flex items-center space-x-1 px-4 py-2 rounded-full text-white ${
              posting || !text.trim()
                ? 'bg-blue-300 cursor-not-allowed'
                : 'bg-blue-600 hover:bg-blue-700'
            }`}
          >
            <PaperAirplaneIcon className="h-5 w-5 transform rotate-90" />
            <span>Post</span>
          </button>
        </div>
      </div>
    </form>
  );
}
