import Rating from './Rating';
import Comments from './Comments';
import '../styles/NewsCard.css';

export default function NewsCard({ story }) {
  return (
    <div className="news-card">
      <div className="news-card__preview">
        {/* Replace with your screenshot service or placeholder */}
        <img
          src={`https://api.allorigins.win/raw?url=${encodeURIComponent(story.url)}`}
          alt={story.title}
        />
      </div>
      <div className="news-card__content">
        <a
          href={story.url}
          target="_blank"
          rel="noopener noreferrer"
          className="news-card__title"
        >
          {story.title}
        </a>
        <p className="news-card__meta">
          by {story.by} â€” {story.score} points
        </p>
        {/* Rating UI */}
                  <Rating itemId={String(story.id)} />
                  {/* Comments UI */}
                  <Comments itemId={String(story.id)} />
      </div>
    </div>
  );
}
