// src/pages/Home.jsx
import React, { useEffect, useState } from 'react';
import { useAuth } from '../auth/AuthProvider';
import SearchBar from '../components/SearchBar';
import StoryList from '../components/StoryList';
import api from '../api';
import '../styles/Home.css';

export default function Home() {
  const { currentUser, logout } = useAuth();
  const [stories, setStories]   = useState([]);
  const [loading, setLoading]   = useState(true);
  const [error, setError]       = useState('');
  const [searchMode, setSearchMode] = useState(false);

  // Move fetchTopStories outside useEffect so it can be reused
  async function fetchTopStories() {
    setLoading(true);
    setError('');
    try {
      const resp = await api.get('/topstories?limit=10');
      setStories(resp.data);
      setSearchMode(false);
    } catch {
      setError('Could not load top stories.');
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    fetchTopStories();
  }, []);

  async function handleSearch(q) {
    setLoading(true);
    setError('');
    try {
      const resp = await api.get('/search', { params: { q, limit: 10 } });
      setStories(resp.data);
      setSearchMode(true);
    } catch {
      setError('Search failed.');
    } finally {
      setLoading(false);
    }
  }

  return (
    <>
      <header className="app-header">
        <div className="app-logo">NewsReader</div>
        <div className="search-container">
          <SearchBar onSearch={handleSearch} />
        </div>
        <div className="app-user">
          <span className="app-user__email">{currentUser.email}</span>
          <button onClick={logout} className="app-user__logout">
            Logout
          </button>
        </div>
      </header>

      <main className="app-main">
        {searchMode && (
          <button onClick={fetchTopStories} className="back-btn">
            ‚Üê Back to Top Stories
          </button>
        )}
        <h2 className="app-title">
          {searchMode ? 'Search Results' : 'Top Stories'}
        </h2>
        <StoryList stories={stories} loading={loading} error={error} />
      </main>
    </>
  );
}
