// src/components/AnimatedComments.jsx
import React, { useState, useEffect } from 'react';
import { useAuth } from '../auth/AuthProvider';
import api from '../api';
import { motion, AnimatePresence } from 'framer-motion';

import { FaSpinner } from 'react-icons/fa';
import Avatar from 'react-avatar';

export default function AnimatedComments({ itemId }) {
  const { currentUser } = useAuth();
  const [comments, setComments]   = useState([]);
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState('');
  const [text, setText]           = useState('');
  const [editingId, setEditingId] = useState(null);
  const [editText, setEditText]   = useState('');

  // fetch & state handlers...
  const fetchComments = async () => {
    setLoading(true);
    try {
      const { data } = await api.get('/comments', { params: { itemId } });
      setComments(data);
    } catch {
      setError('Could not load comments');
    } finally {
      setLoading(false);
    }
  };
  useEffect(fetchComments, [itemId]);

  // Add, update, delete handlers (same as before)‚Ä¶
  // ‚úÖ wrap state updates in animations

  if (loading) {
    return (
      <div className="flex items-center justify-center py-6">
        <FaSpinner className="animate-spin text-2xl text-blue-500" />
      </div>
    );
  }
  if (error) {
    return <p className="text-red-600 text-center">{error}</p>;
  }

  return (
    <div className="mt-6 space-y-6">
      <h3 className="text-xl font-bold">Comments</h3>

      {/* ‚ú® New Comment Input with animated focus */}
      <form onSubmit={async e => { /* handleAdd */ }} className="relative">
        <textarea
          value={text}
          onChange={e => setText(e.target.value)}
          rows={2}
          placeholder="Add a comment‚Ä¶"
          className="peer w-full p-4 border border-transparent rounded-lg shadow-sm
                     focus:outline-none focus:border-blue-400 focus:shadow-lg
                     transition duration-200"
        />
        <motion.button
          type="submit"
          disabled={!text.trim()}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          className="mt-2 ml-auto block px-4 py-2 bg-gradient-to-r from-blue-500 to-indigo-500
                     text-white rounded-full disabled:opacity-50"
        >
          Post
        </motion.button>
      </form>

      {/* ‚ú® List with mount/unmount animations */}
      <AnimatePresence>
        {comments.map(c => (
          <motion.div
            key={c._id}
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.95 }}
            layout
            className="p-4 bg-white border border-gray-200 rounded-lg shadow-sm"
          >
            <div className="flex items-center space-x-3 mb-2">
              <Avatar name={c.userEmail || 'User'} size="36" round />
              <div>
                <p className="text-sm font-medium">{c.userEmail}</p>
                <p className="text-xs text-gray-400">
                  {new Date(c.createdAt).toLocaleString()}
                </p>
              </div>
            </div>

            {/* üîÑ Edit mode toggle */}
            <Transition
              show={editingId === c._id}
              enter="transition-opacity duration-200"
              enterFrom="opacity-0"
              enterTo="opacity-100"
              leave="transition-opacity duration-150"
              leaveFrom="opacity-100"
              leaveTo="opacity-0"
            >
              <form onSubmit={async e => {/* handleUpdate */}} className="space-y-2">
                <textarea
                  rows={2}
                  value={editText}
                  onChange={e => setEditText(e.target.value)}
                  className="w-full p-3 border rounded-lg"
                />
                <div className="flex space-x-2">
                  <button
                    type="submit"
                    className="px-3 py-1 bg-green-500 text-white rounded-md hover:brightness-110 transition"
                  >
                    Save
                  </button>
                  <button
                    type="button"
                    onClick={() => setEditingId(null)}
                    className="px-3 py-1 bg-gray-300 rounded-md hover:bg-gray-400 transition"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </Transition>

            {/* üîÅ Display mode */}
            <Transition
              show={editingId !== c._id}
              enter="transition duration-200"
              enterFrom="opacity-0"
              enterTo="opacity-100"
              leave="hidden"
            >
              <p className="mb-3">{c.text}</p>
              {c.userId === currentUser.uid && (
                <div className="flex space-x-4 text-sm text-blue-500">
                  <button
                    onClick={() => {
                      setEditingId(c._id);
                      setEditText(c.text);
                    }}
                    className="hover:underline"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => {/* handleDelete */}}
                    className="text-red-500 hover:underline"
                  >
                    Delete
                  </button>
                </div>
              )}
            </Transition>
          </motion.div>
        ))}
      </AnimatePresence>
    </div>
  );
}
