// src/components/NewsCard.jsx
import React from 'react';
import Rating from './Rating';
import Comments from './Comments';
import '../styles/NewsCard.css';

export default function NewsCard({ story }) {
  // Use story.image if available, otherwise fallback to a nice placeholder
  const previewImg =
    story.image ||
    'https://images.unsplash.com/photo-1515378791036-0648a3ef77b2?auto=format&fit=crop&w=400&q=80';

  return (
    <div className="news-card">
      <div className="news-card__preview">
        <img
          src={previewImg}
          alt={story.title}
          onError={e => {
            e.target.onerror = null;
            e.target.src =
              'https://images.unsplash.com/photo-1515378791036-0648a3ef77b2?auto=format&fit=crop&w=400&q=80';
          }}
        />
      </div>
      <div className="news-card__content">
        <a
          href={story.url}
          target="_blank"
          rel="noopener noreferrer"
          className="news-card__title"
        >
          {story.title}
        </a>
        <p className="news-card__meta">
          by {story.by} â€” {story.score} points
        </p>
        {/* Rating UI */}
        <Rating itemId={String(story.id)} />
        {/* Comments UI */}
        <Comments itemId={String(story.id)} />
      </div>
    </div>
  );
}
