import React, { useState, useEffect } from 'react';
import { useAuth } from '../auth/AuthProvider';
import api from '../api';
import { motion, AnimatePresence } from 'framer-motion';
import { FaPaperPlane, FaEdit, FaTrashAlt } from 'react-icons/fa';

export default function Comments({ itemId }) {
  const { currentUser } = useAuth();
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [text, setText] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [editText, setEditText] = useState('');

  const fetchComments = async () => {
    setLoading(true);
    try {
      const resp = await api.get('/comments', { params: { itemId } });
      setComments(resp.data);
    } catch (err) {
      setError('Failed to load comments');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchComments();
  }, [itemId]);

  const handleAdd = async e => {
    e.preventDefault();
    if (!text.trim()) return;
    try {
      const token = await currentUser.getIdToken();
      await api.post('/comments', { itemId, text }, { headers: { Authorization: `Bearer ${token}` } });
      setText('');
      fetchComments();
    } catch (err) {
      setError('Failed to post comment');
    }
  };

  const startEdit = (c) => {
    setEditingId(c._id);
    setEditText(c.text);
  };

  const handleUpdate = async e => {
    e.preventDefault();
    try {
      const token = await currentUser.getIdToken();
      await api.put(`/comments/${editingId}`, { text: editText }, { headers: { Authorization: `Bearer ${token}` } });
      setEditingId(null);
      setEditText('');
      fetchComments();
    } catch (err) {
      setError('Failed to update comment');
    }
  };

  const handleDelete = async id => {
    try {
      const token = await currentUser.getIdToken();
      await api.delete(`/comments/${id}`, { headers: { Authorization: `Bearer ${token}` } });
      fetchComments();
    } catch (err) {
      setError('Failed to delete comment');
    }
  };

  if (loading) return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="text-center text-gray-400 font-medium"
    >
      Loading...
    </motion.div>
  );

  if (error) return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="text-center text-red-400 font-medium"
    >
      {error}
    </motion.div>
  );

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ duration: 0.4 }}
      className="mt-6 p-6 bg-gradient-to-br from-gray-800 to-gray-900 rounded-xl shadow-2xl text-white"
    >
      <h4 className="text-2xl font-extrabold mb-4 tracking-tight">Discussion</h4>

      <form onSubmit={handleAdd} className="mb-6">
        <div className="relative group">
          <textarea
            className="w-full p-4 bg-gray-700/50 rounded-lg border border-gray-600 focus:border-blue-400 focus:ring-2 focus:ring-blue-400/50 transition-all duration-300 text-white placeholder-gray-400"
            rows="3"
            placeholder="Join the conversation..."
            value={text}
            onChange={e => setText(e.target.value)}
          />
          <motion.button
            type="submit"
            disabled={!text.trim()}
            whileHover={{ scale: 1.15, rotate: 5 }}
            whileTap={{ scale: 0.9 }}
            className="absolute right-4 bottom-4 p-2 bg-blue-500 rounded-full disabled:opacity-40 transition-colors"
          >
            <FaPaperPlane className="text-lg" />
          </motion.button>
        </div>
      </form>

      <ul className="space-y-4">
        <AnimatePresence>
          {comments.map(c => (
            <motion.li
              key={c._id}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.3 }}
              className="p-4 bg-gray-700/30 rounded-lg border border-gray-600 hover:border-gray-500 transition-all duration-300"
            >
              {editingId === c._id ? (
                <form onSubmit={handleUpdate} className="space-y-3">
                  <textarea
                    className="w-full p-3 bg-gray-600/50 rounded-lg border border-gray-500 focus:border-green-400 focus:ring-2 focus:ring-green-400/50 transition-all text-white"
                    rows="3"
                    value={editText}
                    onChange={e => setEditText(e.target.value)}
                  />
                  <div className="flex space-x-3">
                    <motion.button
                      type="submit"
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      className="px-4 py-2 bg-green-500 rounded-lg flex items-center"
                    >
                      <FaEdit className="mr-2" /> Save
                    </motion.button>
                    <motion.button
                      type="button"
                      onClick={() => setEditingId(null)}
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      className="px-4 py-2 bg-gray-500 rounded-lg"
                    >
                      Cancel
                    </motion.button>
                  </div>
                </form>
              ) : (
                <>
                  <p className="text-gray-200 leading-relaxed">{c.text}</p>
                  <div className="text-xs text-gray-400 mt-2 flex justify-between">
                    <span>{currentUser.email}</span>
                    <span>{new Date(c.createdAt).toLocaleString()}</span>
                  </div>
                  {c.userId === currentUser.uid && (
                    <div className="mt-3 flex space-x-3">
                      <motion.button
                        onClick={() => startEdit(c)}
                        whileHover={{ scale: 1.1, color: '#60A5FA' }}
                        whileTap={{ scale: 0.9 }}
                        className="text-sm flex items-center"
                      >
                        <FaEdit className="mr-1" /> Edit
                      </motion.button>
                      <motion.button
                        onClick={() => handleDelete(c._id)}
                        whileHover={{ scale: 1.1, color: '#F87171' }}
                        whileTap={{ scale: 0.9 }}
                        className="text-sm flex items-center"
                      >
                        <FaTrashAlt className="mr-1" /> Delete
                      </motion.button>
                    </div>
                  )}
                </>
              )}
            </motion.li>
          ))}
        </AnimatePresence>
      </ul>
    </motion.div>
  );
}
