import React, { useState, useEffect } from 'react';
import { FaStar } from 'react-icons/fa';
import api from '../api';
import { useAuth } from '../auth/AuthProvider';

export default function Rating({ itemId }) {
  const { currentUser } = useAuth();
  const [ratings, setRatings]     = useState([]);     // all ratings for this item
  const [userRating, setUserRating] = useState(0);    // current user’s rating
  const [hover, setHover]         = useState(0);      // star hover state
  const [loading, setLoading]     = useState(true);

  // Fetch all ratings for this item
  useEffect(() => {
    const fetchRatings = async () => {
      try {
        const resp = await api.get('/ratings', { params: { itemId } });
        setRatings(resp.data);
        const mine = resp.data.find(r => r.userId === currentUser.uid);
        setUserRating(mine?.value || 0);
      } catch (err) {
        console.error('Could not load ratings', err);
      } finally {
        setLoading(false);
      }
    };
    fetchRatings();
  }, [itemId, currentUser.uid]);

  // Compute average
  const avg =
    ratings.length
      ? (ratings.reduce((sum, r) => sum + r.value, 0) / ratings.length).toFixed(1)
      : '—';

  // Handle click (upsert)
  const handleClick = async (value) => {
    try {
      await api.post('/ratings', { itemId, value }, {
        headers: { Authorization: `Bearer ${await currentUser.getIdToken()}` }
      });
      setUserRating(value);
      // Optionally re-fetch all ratings to update average:
      const resp = await api.get('/ratings', { params: { itemId } });
      setRatings(resp.data);
    } catch (err) {
      console.error('Could not save rating', err);
    }
  };

  if (loading) return <p>Loading rating…</p>;

  return (
    <div className="flex items-center space-x-2 text-sm">
      {/* Stars */}
      <div className="flex">
        {[1,2,3,4,5].map(star => (
          <FaStar
            key={star}
            size={20}
            className="cursor-pointer"
            color={
              hover >= star
                ? '#ffd700'
                : (!hover && userRating >= star)
                  ? '#ffc107'
                  : '#e4e5e9'
            }
            onMouseEnter={() => setHover(star)}
            onMouseLeave={() => setHover(0)}
            onClick={() => handleClick(star)}
          />
        ))}
      </div>
      {/* Average display */}
      <div>
        Avg: {avg} {ratings.length > 0 && <span>({ratings.length})</span>}
      </div>
    </div>
  );
}
