// src/components/Comments.jsx
import React, { useState, useEffect } from 'react';
import { useAuth }            from '../auth/AuthProvider';
import api                    from '../api';
import './Comments.css';

export default function Comments({ itemId }) {
  const { currentUser } = useAuth();

  // State
  const [flatComments, setFlatComments] = useState([]);
  const [commentsTree, setCommentsTree] = useState([]);
  const [loading, setLoading]           = useState(true);
  const [error, setError]               = useState('');
  const [newText, setNewText]           = useState('');
  const [replyTo, setReplyTo]           = useState(null);
  const [replyText, setReplyText]       = useState('');
  const [editingId, setEditingId]       = useState(null);
  const [editText, setEditText]         = useState('');

  // Helper: build nested tree
  const buildTree = list => {
    const map = {};
    list.forEach(c => { map[c._id] = { ...c, replies: [] }; });
    const tree = [];
    list.forEach(c => {
      if (c.parentId) {
        if (map[c.parentId]) map[c.parentId].replies.push(map[c._id]);
      } else {
        tree.push(map[c._id]);
      }
    });
    return tree;
  };

  // Fetch comments
  const fetchComments = async () => {
    setLoading(true);
    try {
      const resp = await api.get('/comments', { params: { itemId } });
      setFlatComments(resp.data);
      setCommentsTree(buildTree(resp.data));
    } catch {
      setError('Could not load comments');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchComments();
  }, [itemId]);

  // Handlers
  const handlePost = async (text, parentId = null) => {
    if (!text.trim()) return;
    try {
      const token = await currentUser.getIdToken();
      await api.post(
        '/comments',
        { itemId, text, parentId },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setNewText('');
      if (parentId) {
        setReplyText('');
        setReplyTo(null);
      }
      fetchComments();
    } catch {
      setError('Could not post comment');
    }
  };

  const handleUpdate = async () => {
    if (!editText.trim()) return;
    try {
      const token = await currentUser.getIdToken();
      await api.put(
        `/comments/${editingId}`,
        { text: editText },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setEditingId(null);
      setEditText('');
      fetchComments();
    } catch {
      setError('Could not update comment');
    }
  };

  const handleDelete = async id => {
    try {
      const token = await currentUser.getIdToken();
      await api.delete(`/comments/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchComments();
    } catch {
      setError('Could not delete comment');
    }
  };

  // Recursive comment node
  const CommentNode = ({ c, depth = 0 }) => (
    <li
      className="comment-card"
      style={{ marginLeft: depth * 16 }}
    >
      {editingId === c._id ? (
        <div className="comment-body">
          <textarea
            className="comment-input"
            rows="2"
            value={editText}
            onChange={e => setEditText(e.target.value)}
          />
          <div className="comment-actions">
            <button onClick={handleUpdate} className="comment-save-btn">
              Save
            </button>
            <button
              onClick={() => setEditingId(null)}
              className="comment-cancel-btn"
            >
              Cancel
            </button>
          </div>
        </div>
      ) : (
        <div className="comment-body">
            <span className="comment-author">
              {c.userEmail ? c.userEmail.split('@')[0] : (c.author || 'User')}
            </span>
          <p className="comment-text">{c.text}</p>
          <div className="comment-meta">
            <span className="comment-time">
              {new Date(c.createdAt).toLocaleString()}
            </span>
          </div>
          <div className="comment-actions">
            <button
              onClick={e => {
                e.stopPropagation();
                setReplyTo(c._id);
                setReplyText('');
              }}
              className="comment-reply-btn"
            >
              Reply
            </button>
            {c.userId === currentUser.uid && (
              <>
                <button
                  onClick={() => {
                    setEditingId(c._id);
                    setEditText(c.text);
                  }}
                  className="comment-edit-btn"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(c._id)}
                  className="comment-delete-btn"
                >
                  Delete
                </button>
              </>
            )}
          </div>
        </div>
      )}

      {/* Reply form */}
      {replyTo === c._id && (
        <form
          className="reply-form"
          onSubmit={e => {
            e.preventDefault();
            handlePost(replyText, replyTo);
          }}
        >
          <textarea
            className="comment-input"
            rows="2"
            placeholder="Write a reply…"
            value={replyText}
            onChange={e => setReplyText(e.target.value)}
          />
          <div className="comment-actions">
            <button type="submit" className="comment-save-btn">
              Post Reply
            </button>
            <button
              type="button"
              onClick={() => setReplyTo(null)}
              className="comment-cancel-btn"
            >
              Cancel
            </button>
          </div>
        </form>
      )}

      {/* Render replies */}
      {c.replies.length > 0 && (
        <ul>
          {c.replies.map(child => (
            <CommentNode key={child._id} c={child} depth={depth + 1} />
          ))}
        </ul>
      )}
    </li>
  );

  // Render
  if (loading) return <p className="comments-loading">Loading comments…</p>;
  if (error)   return <p className="comments-error">{error}</p>;

  return (
    <div className="comments-section">
      <h4 className="comments-title">Comments</h4>

      {/* New comment */}
      <form
        className="comment-form"
        onSubmit={e => {
          e.preventDefault();
          handlePost(newText, null);
        }}
      >
        <textarea
          className="comment-input"
          rows="3"
          placeholder="Add a comment…"
          value={newText}
          onChange={e => setNewText(e.target.value)}
        />
        <button
          type="submit"
          disabled={!newText.trim()}
          className="comment-submit-btn"
        >
          Post
        </button>
      </form>

      {/* Nested comment list */}
      <ul className="comment-list">
        {commentsTree.map(c => (
          <CommentNode key={c._id} c={c} />
        ))}
      </ul>
    </div>
  );
}
