// src/pages/Home.jsx
import React, { useEffect, useState } from 'react';
import { useAuth } from '../auth/AuthProvider';
import api from '../api';
import SearchBar from '../components/SearchBar';
import StoryList from '../components/StoryList';

export default function Home() {
  const { currentUser, logout } = useAuth();
  const [stories, setStories]   = useState([]);
  const [loading, setLoading]   = useState(true);
  const [error, setError]       = useState('');
  const [searchMode, setSearchMode] = useState(false);

  // Fetch top stories on mount
  useEffect(() => {
    fetchTopStories();
  }, []);

  async function fetchTopStories() {
    setLoading(true);
    setError('');
    try {
      const resp = await api.get('/topstories?limit=50');
      setStories(resp.data);
      setSearchMode(false);
    } catch (err) {
      console.error(err);
      setError('Could not load top stories.');
    } finally {
      setLoading(false);
    }
  }

  async function handleSearch(query) {
    setLoading(true);
    setError('');
    try {
      const resp = await api.get('/search', {
        params: { q: query, limit: 10 }
      });
      setStories(resp.data);
      setSearchMode(true);
    } catch (err) {
      console.error(err);
      setError('Search failed.');
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="max-w-3xl mx-auto mt-10 space-y-6">
      {/* Header & Logout */}
      <div className="flex justify-between items-center p-4 border rounded">
        <div>Welcome, <strong>{currentUser.email}</strong></div>
        <button
          onClick={logout}
          className="px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600"
        >
          Log Out
        </button>
      </div>

      {/* Search bar */}
      <section>
        <SearchBar onSearch={handleSearch} />
        {searchMode && (
          <button
            onClick={fetchTopStories}
            className="text-sm underline mb-4"
          >
            ‚Üê Back to Top Stories
          </button>
        )}
      </section>

      {/* Stories */}
      <section>
        <h2 className="text-2xl font-bold mb-4">
          {searchMode ? 'Search Results' : 'Top Stories'}
        </h2>
        <StoryList
          stories={stories}
          loading={loading}
          error={error}
        />
      </section>
    </div>
  );
}
