// src/components/Comments.jsx
import React, { useState, useEffect } from 'react';
import { useAuth } from '../auth/AuthProvider';
import api from '../api';

export default function Comments({ itemId }) {
  const { currentUser } = useAuth();
  const [comments, setComments]   = useState([]);
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState('');
  const [text, setText]           = useState('');
  const [editingId, setEditingId] = useState(null);
  const [editText, setEditText]   = useState('');

  // Fetch comments on mount & after changes
  const fetchComments = async () => {
    setLoading(true);
    try {
      const resp = await api.get('/comments', { params: { itemId } });
      setComments(resp.data);
    } catch (err) {
      console.error(err);
      setError('Could not load comments');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchComments();
  }, [itemId]);

  // Add new comment
  const handleAdd = async e => {
    e.preventDefault();
    if (!text.trim()) return;
    try {
      const token = await currentUser.getIdToken();
      await api.post('/comments',
        { itemId, text },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setText('');
      fetchComments();
    } catch (err) {
      console.error(err);
      setError('Could not post comment');
    }
  };

  // Begin editing
  const startEdit = (c) => {
    setEditingId(c._id);
    setEditText(c.text);
  };

  // Save edited comment
  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const token = await currentUser.getIdToken();
      await api.put(`/comments/${editingId}`,
        { text: editText },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setEditingId(null);
      setEditText('');
      fetchComments();
    } catch (err) {
      console.error(err);
      setError('Could not update comment');
    }
  };

  // Delete a comment
  const handleDelete = async id => {
    try {
      const token = await currentUser.getIdToken();
      await api.delete(`/comments/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchComments();
    } catch (err) {
      console.error(err);
      setError('Could not delete comment');
    }
  };

  if (loading) return <p>Loading comments…</p>;
  if (error)   return <p className="text-red-600">{error}</p>;

  return (
    <div className="mt-4">
      <h4 className="font-semibold mb-2">Comments</h4>

      {/* Add new */}
      <form onSubmit={handleAdd} className="mb-4">
        <textarea
          className="w-full p-2 border rounded mb-2"
          rows="2"
          placeholder="Add a comment…"
          value={text}
          onChange={e => setText(e.target.value)}
        />
        <button
          type="submit"
          disabled={!text.trim()}
          className="px-3 py-1 bg-blue-500 text-white rounded disabled:opacity-50"
        >
          Post
        </button>
      </form>

      {/* List */}
      <ul className="space-y-3">
        {comments.map(c => (
          <li key={c._id} className="p-2 border rounded">
            {editingId === c._id ? (
              <form onSubmit={handleUpdate} className="space-y-2">
                <textarea
                  className="w-full p-2 border rounded"
                  rows="2"
                  value={editText}
                  onChange={e => setEditText(e.target.value)}
                />
                <div className="flex space-x-2">
                  <button type="submit" className="px-2 py-1 bg-green-500 text-white rounded">
                    Save
                  </button>
                  <button
                    type="button"
                    onClick={() => setEditingId(null)}
                    className="px-2 py-1 bg-gray-300 rounded"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            ) : (
              <>
                <p className="mb-1">{c.text}</p>
                <div className="text-xs text-gray-500 flex justify-between">
                  <span>by {currentUser.email}</span>
                  <span>
                    {new Date(c.createdAt).toLocaleString()}
                  </span>
                </div>
                {c.userId === currentUser.uid && (
                  <div className="mt-1 space-x-2">
                    <button
                      onClick={() => startEdit(c)}
                      className="text-sm text-blue-600 hover:underline"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(c._id)}
                      className="text-sm text-red-600 hover:underline"
                    >
                      Delete
                    </button>
                  </div>
                )}
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
