import React, { useState, useEffect } from 'react';
import { useAuth } from '../auth/AuthProvider';
import api from '../api';
import { motion, AnimatePresence } from 'framer-motion';
import { FaUserCircle, FaEdit, FaTrash, FaPaperPlane } from 'react-icons/fa';

export default function Comments({ itemId }) {
  const { currentUser } = useAuth();
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [text, setText] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [editText, setEditText] = useState('');

  // Fetch comments
  const fetchComments = async () => {
    setLoading(true);
    try {
      const resp = await api.get('/comments', { params: { itemId } });
      setComments(resp.data);
    } catch (err) {
      console.error(err);
      setError('Could not load comments');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchComments();
  }, [itemId]);

  // Add comment
  const handleAdd = async e => {
    e.preventDefault();
    if (!text.trim()) return;
    try {
      const token = await currentUser.getIdToken();
      await api.post('/comments', { itemId, text }, { headers: { Authorization: `Bearer ${token}` } });
      setText('');
      fetchComments();
    } catch (err) {
      console.error(err);
      setError('Could not post comment');
    }
  };

  // Start editing
  const startEdit = (c) => {
    setEditingId(c._id);
    setEditText(c.text);
  };

  // Update comment
  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const token = await currentUser.getIdToken();
      await api.put(`/comments/${editingId}`, { text: editText }, { headers: { Authorization: `Bearer ${token}` } });
      setEditingId(null);
      setEditText('');
      fetchComments();
    } catch (err) {
      console.error(err);
      setError('Could not update comment');
    }
  };

  // Delete comment
  const handleDelete = async id => {
    try {
      const token = await currentUser.getIdToken();
      await api.delete(`/comments/${id}`, { headers: { Authorization: `Bearer ${token}` } });
      fetchComments();
    } catch (err) {
      console.error(err);
      setError('Could not delete comment');
    }
  };

  if (loading) return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="text-center text-gray-500"
    >
      Loading comments...
    </motion.div>
  );

  if (error) return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="text-center text-red-500"
    >
      {error}
    </motion.div>
  );

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      className="mt-6 p-4 bg-gray-50 rounded-2xl shadow-lg"
    >
      <h4 className="text-xl font-bold text-gray-800 mb-4">Comments</h4>

      {/* Add comment form */}
      <form onSubmit={handleAdd} className="mb-6">
        <div className="relative">
          <textarea
            className="w-full p-3 pr-12 bg-white rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-300"
            rows="3"
            placeholder="Share your thoughts..."
            value={text}
            onChange={e => setText(e.target.value)}
          />
          <motion.button
            type="submit"
            disabled={!text.trim()}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            className="absolute right-3 bottom-3 p-2 bg-blue-500 text-white rounded-full disabled:opacity-50"
          >
            <FaPaperPlane />
          </motion.button>
        </div>
      </form>

      {/* Comments list */}
      <ul className="space-y-4">
        <AnimatePresence>
          {comments.map(c => (
            <motion.li
              key={c._id}
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: 20 }}
              transition={{ duration: 0.3 }}
              className="p-4 bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow duration-300"
            >
              {editingId === c._id ? (
                <form onSubmit={handleUpdate} className="space-y-3">
                  <textarea
                    className="w-full p-3 bg-gray-50 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-green-500"
                    rows="3"
                    value={editText}
                    onChange={e => setEditText(e.target.value)}
                  />
                  <div className="flex space-x-2">
                    <motion.button
                      type="submit"
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      className="px-4 py-2 bg-green-500 text-white rounded-lg flex items-center"
                    >
                      <FaEdit className="mr-2" /> Save
                    </motion.button>
                    <motion.button
                      type="button"
                      onClick={() => setEditingId(null)}
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      className="px-4 py-2 bg-gray-300 text-gray-800 rounded-lg"
                    >
                      Cancel
                    </motion.button>
                  </div>
                </form>
              ) : (
                <>
                  <div className="flex items-start space-x-3">
                    <FaUserCircle className="text-2xl text-gray-400" />
                    <div className="flex-1">
                      <p className="text-gray-800">{c.text}</p>
                      <div className="text-xs text-gray-500 mt-1 flex justify-between">
                        <span>by {currentUser.email}</span>
                        <span>{new Date(c.createdAt).toLocaleString()}</span>
                      </div>
                    </div>
                  </div>
                  {c.userId === currentUser.uid && (
                    <div className="mt-2 flex space-x-2">
                      <motion.button
                        onClick={() => startEdit(c)}
                        whileHover={{ scale: 1.1 }}
                        whileTap={{ scale: 0.9 }}
                        className="text-sm text-blue-600 hover:text-blue-800 flex items-center"
                      >
                        <FaEdit className="mr-1" /> Edit
                      </motion.button>
                      <motion.button
                        onClick={() => handleDelete(c._id)}
                        whileHover={{ scale: 1.1 }}
                        whileTap={{ scale: 0.9 }}
                        className="text-sm text-red-600 hover:text-red-800 flex items-center"
                      >
                        <FaTrash className="mr-1" /> Delete
                      </motion.button>
                    </div>
                  )}
                </>
              )}
            </motion.li>
          ))}
        </AnimatePresence>
      </ul>
    </motion.div>
  );
}
