// src/components/StoryList.jsx
import React from 'react';
import Rating from './Rating';
import Comments from './Comments';

export default function StoryList({ stories, loading, error }) {
  if (loading) return <p>Loading…</p>;
  if (error)   return <p className="text-red-600">{error}</p>;
  if (!stories.length) return <p>No results found.</p>;

  return (
    <div className="space-y-4">
      {stories.map(story => (
        <div key={story.id} className="p-4 border rounded">
          <a
            href={story.url}
            target="_blank"
            rel="noopener noreferrer"
            className="text-lg font-semibold hover:underline"
          >
            {story.title}
          </a>
          <p className="text-sm text-gray-600">
            by {story.by} — {story.score} points
          </p>
        {/* Rating UI */}
          <Rating itemId={String(story.id)} />
          {/* Comments UI */}
          <Comments itemId={String(story.id)} />
        </div>
      ))}
    </div>
  );
}
