// src/pages/Login.jsx

import React, { useRef, useState } from 'react';
import { useAuth } from '../auth/AuthProvider';
import { Link, useNavigate } from 'react-router-dom';

export default function Login() {
  const emailRef = useRef();
  const passwordRef = useRef();
  const { login } = useAuth();
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setError('');
      setLoading(true);
      await login(emailRef.current.value, passwordRef.current.value);
      navigate('/');
    } catch {
      setError('Failed to log in');
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100 dark:from-gray-900 dark:via-gray-800 dark:to-gray-900">
      <div className="relative w-full max-w-md p-8 rounded-3xl shadow-2xl bg-white/80 dark:bg-gray-900/90 backdrop-blur-lg border border-gray-200 dark:border-gray-800 animate-fade-in">
        <div className="flex flex-col items-center mb-6">
          <img src="/logo192.png" alt="Brackify Logo" className="h-14 w-14 mb-2 animate-bounce" />
          <h2 className="text-3xl font-extrabold text-gray-900 dark:text-white mb-1 tracking-tight">Welcome Back</h2>
          <p className="text-gray-500 dark:text-gray-300 text-sm">Sign in to your Brackify account</p>
        </div>
        {error && <div className="text-red-500 mb-3 text-center font-semibold animate-shake">{error}</div>}
        <form onSubmit={handleSubmit} className="space-y-5">
          <div className="relative">
            <input type="email" ref={emailRef} required className="peer w-full px-4 py-3 rounded-xl border border-gray-300 dark:border-gray-700 bg-white dark:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-purple-400 transition placeholder-transparent" placeholder="Email" />
            <label className="absolute left-4 top-3 text-gray-400 dark:text-gray-500 text-sm transition-all peer-placeholder-shown:top-3 peer-placeholder-shown:text-base peer-focus:-top-4 peer-focus:text-xs peer-focus:text-purple-500 bg-white dark:bg-gray-900 px-1 pointer-events-none">Email</label>
          </div>
          <div className="relative">
            <input type="password" ref={passwordRef} required className="peer w-full px-4 py-3 rounded-xl border border-gray-300 dark:border-gray-700 bg-white dark:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-purple-400 transition placeholder-transparent" placeholder="Password" />
            <label className="absolute left-4 top-3 text-gray-400 dark:text-gray-500 text-sm transition-all peer-placeholder-shown:top-3 peer-placeholder-shown:text-base peer-focus:-top-4 peer-focus:text-xs peer-focus:text-purple-500 bg-white dark:bg-gray-900 px-1 pointer-events-none">Password</label>
          </div>
          <button disabled={loading} type="submit" className="w-full py-3 rounded-xl bg-gradient-to-r from-purple-500 to-pink-500 text-white font-bold shadow-lg hover:from-pink-500 hover:to-purple-500 transition-all text-lg disabled:opacity-60">
            {loading ? 'Logging in...' : 'Log In'}
          </button>
        </form>
        <div className="flex items-center my-6">
          <div className="flex-grow border-t border-gray-300 dark:border-gray-700" />
          <span className="mx-3 text-gray-400 dark:text-gray-500 text-xs">or</span>
          <div className="flex-grow border-t border-gray-300 dark:border-gray-700" />
        </div>
        <button className="w-full flex items-center justify-center gap-2 py-3 rounded-xl bg-white dark:bg-gray-800 border border-gray-300 dark:border-gray-700 shadow hover:bg-gray-50 dark:hover:bg-gray-700 transition-all font-semibold text-gray-700 dark:text-gray-200 mb-2">
          <img src="/google.svg" alt="Google" className="h-5 w-5" />
          Sign in with Google
        </button>
        <p className="mt-6 text-center text-gray-600 dark:text-gray-400">
          Need an account?{' '}
          <Link to="/signup" className="text-purple-600 hover:underline font-semibold">Sign Up</Link>
        </p>
      </div>
    </div>
  );
}
